mapper.select:
SELECT @rownum := IFNULL(@rownum, 0) + 1 AS rownum, c.cnum, c.uid, c.model, c.issue, c.detail, c.visitdate, c.visitend, c.yn, c.prostatus, c.star, c.regdate, c.proname, c.visittime, c.comment, u.user_id, u.username, u.phone, u.address, u.detail AS user_detail FROM customer c JOIN user u ON c.uid = u.uid WHERE u.user_id = #{user_id}
AND c.proname LIKE CONCAT('%', #{keyword}, '%')
AND c.issue LIKE CONCAT('%', #{keyword}, '%')
AND c.prostatus = #{status_keyword}
ORDER BY c.cnum DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*) FROM customer c JOIN user u ON c.uid = u.uid WHERE u.user_id = #{user_id}
AND c.proname LIKE CONCAT('%', #{keyword}, '%')
AND c.issue LIKE CONCAT('%', #{keyword}, '%')
AND c.prostatus = #{status_keyword}

mapper.update:
UPDATE customer SET
visitdate = #{visitdate},  visittime = #{visittime}
WHERE cnum = #{cnum}

UPDATE customer SET
star = #{star}, comment = #{comment}
WHERE cnum = #{cnum}

	  UPDATE customer
    SET visitend = #{endDate}, emp_comment = #{cmt}, prostatus = 'F'
    WHERE cnum = #{cnum}
